        category = dplyr::case_when(
            category == "Fresh_healthy" ~ "Healthy",
            category == "Fresh_icm" ~ "ICM",
            category == "Fresh_nicm" ~ "NICM",
            category == "Fresh_hcm" ~ "HCM"
        ),
        category = factor(
            category,
            levels = c(
                "Healthy", "ICM", "NICM", "HCM"
            )
        )
    ) |&gt;
    ggplot2::ggplot(
        ggplot2::aes(
            x = insert_size,
            y = norm_count,
            color = category
        )
    ) +
    ggplot2::geom_line() +
