fragment_size &lt;- fs::dir_ls(
    path = file.path(
        PROJECT_DIR,
        "qc",
        "fragment_sizes",
        "raw"
    ),
    regexp = "fresh.+_CollectInsertSizeMetrics.txt"
) |&gt;
